import { useFormik } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { useState } from "react";
import ResetPassword from "../ResetPassword/ResetPassword";
import { toast } from "react-toastify";

export default function ForgetPassword({ show, onClose }) {
  const [resetPassword, setResetPassword] = useState(true);
  const [showReset, setShowReset] = useState(false);
  let mySchema;
  if (resetPassword) {
    mySchema = Yup.object({
      email: Yup.string()
        .email("email isn't valid")
        .required("email is required"),
    });
  } else {
    mySchema = Yup.object({
      resetCode: Yup.string()
        .required("resetCode is required")
        .matches(/^[0-9]{6}$/, "resetCode must be 6 numbers"),
    });
  }

  let initialValues = resetPassword ? { email: "" } : { resetCode: "" };
  let formik = useFormik({
    initialValues: initialValues,
    enableReinitialize: true,
    validationSchema: mySchema,
    onSubmit: (values) => {
      getEmail(values);
    },
  });

  let endPoint = resetPassword ? "forgotPasswords" : "verifyResetCode";

  async function getEmail(values) {
    setResetPassword(false);
    return axios
      .post(`https://ecommerce.routemisr.com/api/v1/auth/${endPoint}`, values)
      .then((data) => {
        if (data.data.status === "Success") {
          if (!resetPassword) {
            // Close modal after code is confirmed

            onClose();
            setResetPassword(true);
            formik.resetForm();
            setShowReset(true);
          } else {
            setResetPassword(false);
          }
        }
      })
      .catch((err) => {
        toast.error(err.response.data.message);
        console.log(err);
      });
  }
  if (!show) return null;
  return (
    <>
      <div
        className="modal d-block"
        tabIndex="-1"
        style={{ backgroundColor: "#00000080" }}
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content p-3">
            <div className="modal-header">
              <h5 className="modal-title">Forgot Password</h5>
              <button
                type="button"
                className="btn-close"
                onClick={() => {
                  // Close modal
                  onClose();

                  // Reset all states and form values
                  setResetPassword(true); // Go back to "email" step
                  setShowReset(false); // Hide reset modal if open
                  formik.resetForm(); // Clear form values and touched/errors
                }}
              ></button>
            </div>
            <div className="modal-body">
              <form onSubmit={formik.handleSubmit}>
                <div className="mb-3">
                  {resetPassword ? (
                    <>
                      <input
                        type="email"
                        className="form-control"
                        placeholder="Enter your email"
                        name="email"
                        value={formik.values.email}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                      />
                      {formik.touched.email && formik.errors.email && (
                        <p className="text-danger">{formik.errors.email}</p>
                      )}
                    </>
                  ) : (
                    <>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Enter reset code"
                        name="resetCode"
                        value={formik.values.resetCode}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                      />
                      {formik.touched.resetCode && formik.errors.resetCode && (
                        <p className="text-danger">{formik.errors.resetCode}</p>
                      )}
                    </>
                  )}
                </div>
                <button
                  type="submit"
                  className="btn bg-main w-100 "
                  disabled={!(formik.isValid && formik.dirty)}
                >
                  {resetPassword ? "Next" : "Confirm"}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
      {/* <ResetPassword show={showReset} onClose={() => setShowReset(false)} /> */}
    </>
  );
}
